# Откройте файл для чтения
with open('1 - 1.csv.csv', 'r', encoding='utf-8') as file:
    # Прочитайте каждую строку
    for line in file:
        # Токенизируйте строку
        words = nltk.word_tokenize(line)

        # Удалите стоп-слова из токенов
