import pandas as pd

# Функция для обработки CSV файла
def process_csv(file_path):
    # Читаем CSV файл
    df = pd.read_csv(file_path)

    # Проверяем, существует ли колонка 'text'
    if 'text' not in df.columns:
        raise ValueError("CSV файл должен содержать колонку 'text'.")

    # Подготовка списка для хранения результатов
    tokenized_result = []

    # Обрабатываем каждую строку в колонке 'text'
    for original_text in df['text']:
        # Токенизуем текст на слова
        tokens = original_text.split()  # или используйте .split() для простого разделения по пробелу
        tokenized_result.append(tokens)

    # Создаем новый DataFrame с результатами
    result_df = pd.DataFrame({'Tokens': tokenized_result})

    # Сохраняем результат в новый CSV файл
    result_df.to_csv('tokenized_output.csv', index=False)
    print("Результаты сохранены в файл 'tokenized_output.csv'.")

